import React, { useEffect, useState } from "react";
import { View, Text, Button, Animated } from "react-native";
import { useSelector, useDispatch } from "react-redux";
import { RootState } from "../../store";
import { updateProgress } from "../../store/progress-slice";
import styles from "./styles";

export default function ProgressTracking({ data = [] }) {
  const topics =
    data.length > 0
      ? data
      : useSelector((state: RootState) => state.progress.topics);
  const dispatch = useDispatch();

  const handleUpdateProgress = (topicName: string) => {
    // Генерируем случайное число от 0 до 1 и масштабируем его до процентов
    const progress = Math.random() * 100;
    dispatch(updateProgress({ topicName, progress }));
  };

  return (
    <View style={styles.container}>
      {topics.map((topic, index) => (
        <View key={index} style={styles.topicContainer}>
          <Text style={styles.topic}>
            {topic.name}: {topic.progress.toFixed(2)}%{" "}
          </Text>
          <Button
            title="Update Progress"
            onPress={() => handleUpdateProgress(topic.name)}
          />
        </View>
      ))}
    </View>
  );
}

export function ProgressAnimation({ color = "blue", duration = 5000 }) {
  const progress = useState(new Animated.Value(0))[0];

  useEffect(() => {
    Animated.timing(progress, {
      toValue: 100,
      duration,
      useNativeDriver: false
    }).start();
  }, [progress, duration]);

  return (
    <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
      <Animated.View
        style={{
          width: "100%",
          height: 30,
          backgroundColor: "gray",
          overflow: "hidden"
        }}
      >
        <Animated.View
          style={[
            {
              width: progress.interpolate({
                inputRange: [0, 100],
                outputRange: ["0%", "100%"]
              }),
              height: 30,
              backgroundColor: color
            }
          ]}
        />
      </Animated.View>
      <Animated.Text style={{ marginTop: 10 }}>
        {progress.interpolate({
          inputRange: [0, 100],
          outputRange: ["0%", "100%"]
        })}
      </Animated.Text>
    </View>
  );
}
