// /screens/WordCollectionScreen.tsx
import React, { useState } from "react";
import { View, Text, TouchableOpacity } from "react-native";
import DraggableFlatList from "react-native-draggable-flatlist";
import { SelectedWordsType } from "../../types/types";
import styles from "./styles";

export default function WordCollectionScreen() {
  const correctSentence = "Hello World";
  const [selectedWords, setSelectedWords] = useState<SelectedWordsType>([]);
  const [data, setData] = useState([{ key: "Hello" }, { key: "World" }]);

  const handleCheck = () => {
    const sentence = selectedWords.join(" ");
    if (sentence === correctSentence) {
      alert("Correct!");
    } else {
      alert("Try again.");
    }
  };

  return (
    <View style={styles.container}>
      <DraggableFlatList
        data={data}
        renderItem={({ item, drag }) => (
          <TouchableOpacity
            onLongPress={drag}
            onPress={() => setSelectedWords([...selectedWords, item.key])}
          >
            <Text>{item.key}</Text>
          </TouchableOpacity>
        )}
        keyExtractor={(item) => `draggable-item-${item.key}`}
        onDragEnd={({ data }) => setData(data)}
      />
      <TouchableOpacity onPress={handleCheck} style={styles.button}>
        <Text style={styles.buttonText}>Check</Text>
      </TouchableOpacity>
    </View>
  );
}
