// / ProgressAnimation.js
import React, { useEffect } from "react";
import { View, Animated } from "react-native";
import { useSelector } from "react-redux";
import { RootState } from "../../store";
import styles from "./styles";

export default function ProgressAnimation({ color = "blue", duration = 5000 }) {
  const progressValue = useSelector(
    (state: RootState) => state.progress.progressValue
  );
  const progress = new Animated.Value(progressValue);

  useEffect(() => {
    Animated.timing(progress, {
      toValue: progressValue,
      duration,
      useNativeDriver: false
    }).start();
  }, [progress, progressValue, duration]);

  return (
    <View style={styles.container}>
      <Animated.View
        style={{
          width: "100%",
          height: 30,
          backgroundColor: "gray",
          overflow: "hidden"
        }}
      >
        <Animated.View
          style={[
            {
              width: progress.interpolate({
                inputRange: [0, 100],
                outputRange: ["0%", "100%"]
              }),
              height: 30,
              backgroundColor: color
            }
          ]}
        />
      </Animated.View>
      <Animated.Text style={{ marginTop: 10 }}>
        {progress.interpolate({
          inputRange: [0, 100],
          outputRange: ["0%", "100%"]
        })}
      </Animated.Text>
    </View>
  );
}
